(21, 0, 'So Placeholder', 'ocmod-soplaceholder', '', '1.0.0', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So Placeholder]]></name> \n <code>ocmod-soplaceholder</code> \n <version><![CDATA[1.0.0]]></version> \n \n<!--========Catalog/controller/product=============--> \n <file path=\"catalog/controller/product/category.php\"> \n <operation> \n <search><![CDATA[$image = $this->model_tool_image->resize(\'placeholder.png\', $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));]]></search> \n <add position=\"replace\"><![CDATA[$image = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));]]></add> \n </operation> \n <operation > \n <search ><![CDATA[// Set the last category breadcrumb]]></search> \n <add position=\"before\"><![CDATA[ \n $placeholder=\'placeholder.png\'; \n if($this->soconfig->get_settings(\'placeholder_status\')){ \n $placeholder = $this->soconfig->get_settings(\'placeholder_img\'); \n }else{ \n $placeholder = \'placeholder.png\'; \n } \n ]]> \n </add> \n </operation> \n <operation > \n <search ><![CDATA[$data[\'thumb\'] = \'\';]]></search> \n <add position=\"replace\"><![CDATA[ \n $data[\'thumb\'] = $this->model_tool_image->resize($placeholder, $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_category_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_category_height\')); \n ]]> \n </add> \n </operation> \n <operation > \n <search ><![CDATA[$data[\'categories\'][] ]]></search> \n <add position=\"before\"><![CDATA[ \n if ($result[\'image\']) $image = $this->model_tool_image->resize($result[\'image\'], $this->config->get(