link(\'information/information/agree\', \'information_id=\' . $this->config->get(\'config_account_id\'), true), $information_info[\'title\'], $information_info[\'title\']); \n } else { \n $data[\'text_privacy\'] = \'\'; \n } \n } else { \n $data[\'text_privacy\'] = \'\'; \n } \n \n if ($this->config->get(\'config_checkout_id\')) { \n $this->load->model(\'catalog/information\'); \n \n $information_info = $this->model_catalog_information->getInformation($this->config->get(\'config_checkout_id\')); \n \n if ($information_info) { \n $data[\'text_agree\'] = sprintf($this->language->get(\'text_agree\'), $this->url->link(\'information/information/agree\', \'information_id=\' . $this->config->get(\'config_checkout_id\'), true), $information_info[\'title\'], $information_info[\'title\']); \n } else { \n $data[\'text_agree\'] = \'\'; \n } \n } else { \n $data[\'text_agree\'] = \'\'; \n } \n \n if ($data[\'text_privacy\'] === $data[\'text_agree\']) { \n $data[\'text_privacy\'] = \'\'; \n } \n \n $data[\'text_comments\'] = $this->language->get(\'text_comments\'); \n \n if ($this->isLoggedIn()) { \n $data[\'entry_newsletter\'] = false; \n } else { \n $data[\'entry_newsletter\'] = sprintf($this->language->get(\'entry_newsletter\'), $this->config->get(\'config_name\')); \n } \n \n $data[\'comment\'] = $this->model_extension_module_so_onepagecheckout->getComment(); \n \n if (isset($this->session->data[\'error\'])) { \n $data[\'error_warning\'] = $this->session->data[\'error\']; \n unset($this->session->data[\'error\']); \n } else { \n $data[\'error_warning\'] = \'\'; \n } \n \n $data[\'customer_groups\'] = array(); \n if (is_array($this->config->get(\'config_customer_group_display\'))) { \n $this->load->model(\'account/customer_group\'); \n \n $customer_groups = $this->model_account_customer_group->getCustomerGroups(); \n \n foreach ($customer_groups as $customer_group) { \n if (in_array($customer_group[\'customer_group_id\'], $this->config->get(\'config_customer_group_display\'))) { \n $data[\'customer_groups\'][] = $customer_group; \n } \n } \n } \n if (isset($this->request->post[\'customer_group_id\'])) { \n $data[\'customer_group_id\'] = $this->request->post[\'customer_group_id\']; \n } else { \n $data[\'customer_group_id\'] = $this->config->get(\'config_customer_group_id\'); \n } \n \n \n \n //Get cart \n $this->load->model(\'tool/image\'); \n $this->load->model(\'tool/upload\'); \n \n $data[\'products\'] = array(); \n $products = $this->cart->getProducts(); \n foreach ($products as $product) { \n $product_total = 0; \n \n foreach ($products as $product_2) { \n if ($product_2[\'product_id\'] == $product[\'product_id\']) { \n $product_total += $product_2[\'quantity\']; \n } \n } \n \n if ($product[\'minimum\'] > $product_total) { \n $data[\'error_warning\'] = sprintf($this->language->get(\'error_minimum\'), $product[\'name\'], $product[\'minimum\']); \n } \n \n if ($product[\'image\']) { \n $image = $this->model_tool_image->resize($product[\'image\'], $this->config->get(\'theme_\'.$this->config->get(\'config_theme\') . \'_image_cart_width\'), $this->config->get(\'theme_\'.$this->config->get(\'config_theme\') . \'_image_cart_height\')); \n } else { \n $image = \'\'; \n } \n \n $option_data = array(); \n \n foreach ($product[\'option\'] as $option) { \n if ($option[\'type\'] != \'file\') { \n $value = $option[\'value\']; \n } else { \n $upload_info = $this->model_tool_upload->getUploadByCode($option[\'value\']); \n \n if ($upload_info) { \n $value = $upload_info[\'name\']; \n } else { \n $value = \'\'; \n } \n } \n \n $option_data[] = array( \n \'name\' => $option[\'name\'], \n \'value\' => (utf8_strlen($value) > 20 ? utf8_substr($value, 0, 20) . \'..\' : $value) \n ); \n } \n \n // Display prices \n if ($this->customer->isLogged() || !$this->config->get(\'config_customer_price\')) { \n $unit_price = $this->tax->calculate($product[\'price\'], $product[\'tax_class_id\'], $this->config->get(\'config_tax\')); \n \n $price = $this->currency->format($unit_price, $this->session->data[\'currency\']); \n $total = $this->currency->format($unit_price * $product[\'quantity\'], $this->session->data[\'currency\']); \n } else { \n $price = false; \n $total = false; \n } \n \n $recurring = \'\'; \n \n if ($product[\'recurring\']) { \n $frequencies = array( \n \'day\' => $this->language->get(\'text_day\'), \n \'week\' => $this->language->get(\'text_week\'), \n \'semi_month\' => $this->language->get(\'text_semi_month\'), \n \'month\' => $this->language->get(\'text_month\'), \n \'year\' => $this->language->get(\'text_year\'), \n ); \n \n if ($product[\'recurring\'][\'trial\']) { \n $recurring = sprintf($this->language->get(\'text_trial_description\'), $this->currency->format($this->tax->calculate($product[\'recurring\'][\'trial_price\'] * $product[\'quantity\'], $product[\'tax_class_id\'], $this->config->get(\'config_tax\')), $this->session->data[\'currency\']), $product[\'recurring\'][\'trial_cycle\'], $frequencies[$product[\'recurring\'][\'trial_frequency\']], $product[\'recurring\'][\'trial_duration\']) . \' \'; \n } \n \n if ($product[\'recurring\'][\'duration\']) { \n $recurring .= sprintf($this->language->get(\'text_payment_description\'), $this->currency->format($this->tax->calculate($product[\'recurring\'][\'price\'] * $product[\'quantity\'], $product[\'tax_class_id\'], $this->config->get(\'config_tax\')), $this->session->data[\'currency\']), $product[\'recurring\'][\'cycle\'], $frequencies[$product[\'recurring\'][\'frequency\']], $product[\'recurring\'][\'duration\']); \n } else { \n $recurring .= sprintf($this->language->get(\'text_payment_cancel\'), $this->currency->format($this->tax->calculate($product[\'recurring\'][\'price\'] * $product[\'quantity\'], $product[\'tax_class_id\'], $this->config->get(\'config_tax\')), $this->session->data[\'currency\']), $product[\'recurring\'][\'cycle\'], $frequencies[$product[\'recurring\'][\'frequency\']], $product[\'recurring\'][\'duration\']); \n } \n } \n \n $data[\'products\'][] = array( \n \'cart_id\' => $product[\'cart_id\'], \n \'thumb\' => $image, \n \'name\' => $product[\'name\'], \n \'model\' => $product[\'model\'], \n \'option\' => $option_data, \n \'recurring\' => $recurring, \n \'quantity\' => $product[\'quantity\'], \n \'stock\' => $product[\'stock\'] ? true : !(!$this->config->get(\'config_stock_checkout\') || $this->config->get(\'config_stock_warning\')), \n \'reward\' => ($product[\'reward\'] ? sprintf($this->language->get(\'text_points\'), $product[\'reward\']) : \'\'), \n \'price\' => $price, \n \'total\' => $total, \n \'href\' => $this->url->link(\'product/product\', \'product_id=\' . $product[\'product_id\']) \n ); \n } \n \n // Gift Voucher \n $data[\'vouchers\'] = array(); \n \n if (!empty($this->session->data[\'vouchers\'])) { \n foreach ($this->session->data[\'vouchers\'] as $key => $voucher) { \n $data[\'vouchers\'][] = array( \n \'key\' => $key, \n \'description\' => $voucher[\'description\'], \n \'amount\' => $this->currency->format($voucher[\'amount\'], $this->session->data[\'currency\']), \n \'remove\' => $this->url->link(\'checkout/cart\', \'remove=\' . $key) \n ); \n } \n } \n \n // Totals \n $this->load->model(\'setting/extension\'); \n \n $totals = array(); \n $taxes = $this->cart->getTaxes(); \n $total = 0; \n \n // Because __call can not keep var references so we put them into an array. \n $total_data = array( \n \'totals\' => &$totals, \n \'taxes\' => &$taxes, \n \'total\' => &$total \n ); \n \n // Display prices \n if ($this->customer->isLogged() || !$this->config->get(\'config_customer_price\')) { \n $sort_order = array(); \n \n $results = $this->model_setting_extension->getExtensions(\'total\'); \n \n foreach ($results as $key => $value) { \n $sort_order[$key] = $this->config->get(\'total_\'.$value[\'code\'] . \'_sort_order\'); \n } \n \n array_multisort($sort_order, SORT_ASC, $results); \n \n foreach ($results as $result) { \n if ($this->config->get(\'total_\'.$result[\'code\'] . \'_status\')) { \n $this->load->model(\'extension/total/\' . $result[\'code\']); \n \n // We have to put the totals in an array so that they pass by reference. \n $this->{\'model_extension_total_\' . $result[\'code\']}->getTotal($total_data); \n } \n } \n \n $sort_order = array(); \n \n foreach ($totals as $key => $value) { \n $sort_order[$key] = $value[\'sort_order\']; \n } \n \n array_multisort($sort_order, SORT_ASC, $totals); \n } \n \n $data[\'totals\'] = array(); \n \n foreach ($totals as $total) { \n $data[\'totals\'][] = array( \n \'title\' => $total[\'title\'], \n \'text\' => $this->currency->format($total[\'value\'], $this->session->data[\'currency\']) \n ); \n } \n } \n } \n ]]></add> \n </operation> \n \n <operation error=\"log\"> \n <search ><![CDATA[$this->response->setOutput($this->load->view(\'checkout/checkout\', $data));]]></search> \n <add position=\"replace\"><![CDATA[ \n if (isset($setting_so_onepagecheckout_general[\'so_onepagecheckout_enabled\']) && $setting_so_onepagecheckout_general[\'so_onepagecheckout_enabled\']) { \n $data[\'forgotten\'] = $this->url->link(\'account/forgotten\', \'\', true); \n $this->model_extension_module_so_onepagecheckout->save(); \n if ($setting_so_onepagecheckout_general[\'so_onepagecheckout_layout\'] == 1) \n $this->response->setOutput($this->load->view(\'extension/module/so_onepagecheckout/default\', $data)); \n else if ($setting_so_onepagecheckout_general[\'so_onepagecheckout_layout\'] == 2) \n $this->response->setOutput($this->load->view(\'extension/module/so_onepagecheckout/default2\', $data)); \n } \n else { \n $this->response->setOutput($this->load->view(\'checkout/checkout\', $data)); \n } \n ]]></add> \n </operation> \n </file> \n</modification>', 1, '2018-07-07 15:56:35', '2018-07-25 14:05:35');
INSERT INTO `oc_modification` (`modification_id`, `extension_install_id`, `name`, `code`, `author`, `version`, `link`, `xml`, `status`, `date_added`, `date_modified`) VALUES
(7, 0, 'So Mobile', 'so-mobile', '', '1.1.0', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name><![CDATA[So Mobile]]></name> \n <code>so-mobile</code> \n <version><![CDATA[1.1.0]]></version> \n \n <!--========Catalog/controller/common=============--> \n <file path=\"catalog/controller/common/home.php\"> \n <operation > \n <search ><![CDATA[$this->response->setOutput($this->load->view(\'common/home\', $data));]]></search> \n <add position=\"replace\"><![CDATA[ \n $soconfig = $this->soconfig; \n $platforms_mobile = $soconfig->get_settings(\'platforms_mobile\'); \n if($this->session->data[\'device\']==\'mobile\' && $platforms_mobile) $this->response->redirect($this->url->link(\'extension/mobile/home\')); \n else $this->response->setOutput($this->load->view(\'common/home\', $data)); \n ]]></add> \n </operation> \n </file> \n \n <file path=\"catalog/controller/common/header.php\"> \n <operation > \n <search ><![CDATA[return $this->load->view(\'common/header\', $data)]]></search> \n <add position=\"before\"><![CDATA[ \n $this->load->language(\'extension/soconfig/somobile\'); \n $data[\'menu_search\'] = $this->url->link(\'product/search\', \'\', true); \n $data[\'mobile\'] = $this->soconfig; \n $http = $_SERVER[\"HTTPS\"] ? \'https://\' : \'http://\'; \n $data[\'actual_link\'] = $http.\"$_SERVER[HTTP_HOST]$_SERVER[REQUEST_URI]\"; \n $data[\'text_items\'] = sprintf($this->language->get(\'text_itemcount\'), $this->cart->countProducts() + (isset($this->session->data[\'vouchers\']) ? count($this->session->data[\'vouchers\']) : 0)); \n if($this->session->data[\'device\']==\'mobile\'){ \n $data[\'home\'] = $this->url->link(\'extension/mobile/home\'); \n }else{ \n $data[\'home\'] = $this->url->link(\'common/home\'); \n } \n \n ]]></add> \n </operation> \n </file> \n \n <file path=\"catalog/controller/common/footer.php\"> \n <operation> \n <search ><![CDATA[return $this->load->view(\'common/footer\', $data); ]]></search> \n <add position=\"before\"><![CDATA[ \n // Menu Mobile \n //Decodes HTML Entities \n $data[\'customfooter_text\'] = html_entity_decode($data[\'soconfig\']->get_settings(\'customfooter_text\'), ENT_QUOTES, \'UTF-8\'); \n \n ]]></add> \n </operation> \n \n </file> \n <!--End Catalog/controller/common--> \n \n <!--Catalog/controller/product--> \n <file path=\"catalog/controller/product/category.php\"> \n <operation> \n <search ><![CDATA[$data[\'heading_title\'] = $category_info[\'name\']]]></search> \n <add position=\"before\"><![CDATA[$data[\'mobile\'] =$this->soconfig;]]></add> \n </operation> \n </file> \n <file path=\"catalog/controller/product/{search,special,manufacturer}*.php\"> \n <operation> \n <search ><![CDATA[$this->load->model(\'tool/image\')]]></search> \n <add position=\"after\"><![CDATA[$data[\'mobile\'] = $this->soconfig;]]></add> \n </operation> \n \n </file> \n \n <!--End Catalog/controller/product--> \n \n</modification> ', 1, '2017-08-14 16:26:05', '2018-07-25 14:03:10'),
(11, 0, 'So Tools', 'ocmod-so-Tools', '', '1.0.0', '', '<?xml version=\"1.0\" encoding=\"utf-8\"?> \n<modification> \n <name>So Tools</name> \n <code>ocmod-so-Tools</code> \n <version>1.0.0</version> \n \n <file path=\"catalog/controller/product/product.php\"> \n <operation> \n <search><![CDATA[$this->load->model(\'catalog/manufacturer\');]]></search> \n <add position=\"after\"><![CDATA[ \n $so_recent_products = array(); \n if (isset($this->request->cookie[\'sorecentproduct\'])) { \n $so_recent_products = explode(\',\', $this->request->cookie[\'sorecentproduct\']); \n } else if (isset($this->session->data[\'sorecentproduct\'])) { \n $so_recent_products = $this->session->data[\'sorecentproduct\']; \n } \n \n if (isset($this->request->get[\'route\']) && $this->request->get[\'route\'] == \'product/product\') { \n $product_id = $this->request->get[\'product_id\']; \n $so_recent_products = array_diff($so_recent_products, array($product_id)); \n array_unshift($so_recent_products, $product_id); \n setcookie(\'sorecentproduct\', implode(\',\', $so_recent_products), time() + 60 * 60 * 24 * 30, \'/\', $this->request->server[\'HTTP_HOST\']); \n if (!isset($this->session->data[\'sorecentproduct\']) || $this->session->data[\'sorecentproduct\'] != $so_recent_products) { \n $this->session->data[\'sorecentproduct\'] = $so_recent_products; \n } \n } \n ]]></add> \n </operation> \n </file> \n \n \n \n</modification>', 1, '2017-09-05 14:50:51', '2018-07-25 14:05:03'),
